DROP DATABASE IF EXISTS `bluegraded`;
CREATE DATABASE IF NOT EXISTS `bluegraded`;
USE `bluegraded`;

DROP TABLE IF EXISTS `actions`;
CREATE TABLE IF NOT EXISTS `actions` (
  `id_action` INT NOT NULL AUTO_INCREMENT,
  `user` INT NOT NULL,
  `action_type` INT NOT NULL,
  `time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id_action`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `feedback`;
CREATE TABLE IF NOT EXISTS `feedback` (
  `id_feedback` INT NOT NULL AUTO_INCREMENT,
  `feedbacktype` INT DEFAULT NULL COMMENT '0=Like 1=Report 2=Comment',
  `content_id` INT NOT NULL COMMENT 'id_Contenido',
  `type_content` INT NOT NULL COMMENT '0 = POST 1 = PROJECT',
  `votes` INT NOT NULL COMMENT 'Owner del Post',
  `viewer` INT NOT NULL COMMENT 'Usuario Que Vota',
  `creator` INT NOT NULL COMMENT 'CREADOR DEL POST',
  `time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id_feedback`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `logs`;
CREATE TABLE IF NOT EXISTS `logs` (
  `id_log` INT NOT NULL AUTO_INCREMENT,
  `type` INT NOT NULL COMMENT '0 = CREATE, 1 = UPDATE, 2 = DELETE, 3 = HIDE, 4 = SHOW, 5 = REPORT, 6 = LIKE',
  `user` INT NOT NULL COMMENT 'id_user',
  `content` INT NOT NULL COMMENT 'id_content',
  `content_type` INT NOT NULL COMMENT '0 = POST, 1 = PROJECT, 2 = BLOG, 3 = WEBPAGE, 4 = DEMO',
  `ip` VARBINARY(16) NOT NULL COMMENT 'IP USER',
  `time` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id_log`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `mod_table`;
CREATE TABLE IF NOT EXISTS `mod_table` (
  `id_moderation` INT NOT NULL AUTO_INCREMENT,
  `user_id` INT NOT NULL,
  `rol` VARCHAR(80) NOT NULL DEFAULT 'MODERADOR' COMMENT 'MODERADOR/ADMIN',
  `adelete` TINYINT(1) NOT NULL DEFAULT 0 COMMENT '1=SI 0=NO',
  `aedit` TINYINT(1) NOT NULL DEFAULT 0 COMMENT '1=SI 0=NO',
  `ahide` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '1=SI 0=NO',
  `ashow` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '1=SI 0=NO',
  `aban` TINYINT(1) NOT NULL DEFAULT 0 COMMENT '1=SI 0=NO',
  PRIMARY KEY (`id_moderation`),
  UNIQUE KEY `user_id` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
