DROP TABLE IF EXISTS `posts`;
CREATE TABLE IF NOT EXISTS `posts` (
  `id_post` INT NOT NULL AUTO_INCREMENT,
  `user` INT NOT NULL,
  `title` VARCHAR(80) NOT NULL DEFAULT '',
  `text` TEXT NOT NULL,
  `points` INT NOT NULL DEFAULT 0 COMMENT 'Puntuacion del Post',
  `reports` INT NOT NULL DEFAULT 0 COMMENT 'Reportes',
  `ip` VARCHAR(50) DEFAULT NULL,
  `private` TINYINT NOT NULL DEFAULT 0 COMMENT '0 = PUBLICO, 1 = PRIVADO, 2 = ONLY_FOLLOWERS, 3 = DELETED, 4 = BANNED',
  PRIMARY KEY (`id_post`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `register`;
CREATE TABLE IF NOT EXISTS `register` (
  `id_usr` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `username` VARCHAR(50) NOT NULL,
  `name` VARCHAR(50) DEFAULT NULL,
  `surname` VARCHAR(50) DEFAULT NULL,
  `passwd` VARCHAR(255) NOT NULL,
  `email` VARCHAR(100) NOT NULL,
  `usr_img` TEXT NOT NULL,
  `usr_bio` LONGTEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `ip` VARCHAR(45) DEFAULT NULL,
  `fecha_solicitud` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `estado` TINYINT NOT NULL DEFAULT 0 COMMENT '0 = Pendiente, 1 = Revisando, 2 = Pendiente de Info, 3 = Rechazado',
  `color` VARCHAR(7) NOT NULL,
  `accepted` TINYINT NOT NULL DEFAULT 0,
  PRIMARY KEY (`id_usr`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `usr`;
CREATE TABLE IF NOT EXISTS `usr` (
  `id_usr` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `username` VARCHAR(50) NOT NULL,
  `name` VARCHAR(50) DEFAULT NULL,
  `surname` VARCHAR(50) DEFAULT NULL,
  `passwd` VARCHAR(255) NOT NULL,
  `email` VARCHAR(100) NOT NULL,
  `usr_img` TEXT NOT NULL,
  `usr_bio` LONGTEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `usr_data` LONGTEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_bin,
  `reputation` INT NOT NULL DEFAULT 0,
  `ip` VARCHAR(45) DEFAULT NULL,
  `token` VARCHAR(80) NOT NULL,
  PRIMARY KEY (`id_usr`),
  UNIQUE KEY `username` (`username`, `token`, `id_usr`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;




